package intervalo300;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Problema369 {

	static StringBuilder sb = new StringBuilder(50000);
	static BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
	static String line;
	static final String SALTO = "\n";
	static final String[] N1 = new String[] { "\n", "1\n", "11\n", "111\n", "1111\n", "11111\n", "111111\n",
			"1111111\n", "11111111\n", "111111111\n" };
	static final String[] N10 = new String[] { "", "1111111111", "11111111111111111111",
			"111111111111111111111111111111", "1111111111111111111111111111111111111111",
			"11111111111111111111111111111111111111111111111111",
			"111111111111111111111111111111111111111111111111111111111111",
			"1111111111111111111111111111111111111111111111111111111111111111111111",
			"11111111111111111111111111111111111111111111111111111111111111111111111111111111",
			"111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111" };
	static final String[] N100 = new String[] { "",
			"1111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111",
			"11111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111",
			"",
			"",
			"",
			"",
			"",
			"",
			"",
			"" };

	static int r;
	static int n;
	// Integer.parseInt
	static final int RADIX = 10;
	static int result;
	static int i;
	static int len;
	static int digit;

	public static void main(String[] args) throws IOException {
		while ((line = in.readLine()) != null) {
			// start n = Integer.parseInt(line);
			len = line.length();
			i = 0;
			result = 0;
			while (i < len) {
				digit = Character.digit(line.charAt(i++), RADIX);
				result *= RADIX;
				result -= digit;
			}
			n = -result;
			// end
			if (n == 0)
				break;
			r = n / 100;
			n = n % 100;
			sb.append(N100[r]);
			r = n / 10;
			n = n % 10;
			sb.append(N10[r]);
			sb.append(N1[n]);
		}
		System.out.print(sb.toString());
	}
}